// Assuming you have a PSR-4 autoloader in place\nuse App\Widgets;\n\n// Fetch widgets from the database\n$widgets = $db->query('SELECT name, column_position, display_order FROM widgets ORDER BY column_position, display_order')->fetchAll();\n\nforeach ($widgets as $widget) {\n    $widgetClass = 'App\\Widgets\\' . $widget['name'];\n    if (class_exists($widgetClass)) {\n        echo $widgetClass::run();\n    } else {\n        // Handle error: widget class not found\n        echo 'Widget not found: ' . htmlspecialchars($widget['name']);\n    }\n}