<?php\n\nfunction getComposedTerms($sentence, $limit) {\n    $words = explode(' ', $sentence);\n    $numWords = count($words);\n    $combinations = [];\n\n    for ($i = 0; $i < $numWords; $i++) {\n        for ($j = 1; $j <= $limit; $j++) {\n            $term = [];\n            for ($k = 0; $k < $j && $i + $k < $numWords; $k++) {\n                $term[] = $words[$i + $k];\n            }\n            if (count($term) == $j) {\n                $combinations[] = implode(' ', $term);\n            }\n        }\n    }\n\n    return $combinations;\n}\n\n$sentence = "John Doe moved to New York last year";\n$limit = 3;\n$composedTerms = getComposedTerms($sentence, $limit);\n\nprint_r($composedTerms);\n?>