$php_files_and_content = new PhpFileAndContentIterator($dir);\n$defined_classes = new DefinedClassDetector($php_files_and_content);\n\n$classes_and_paths = [];\n\nforeach ($defined_classes as $fileClasses) {\n    foreach ($fileClasses['classes'] as $class) {\n        $classes_and_paths[$class] = $fileClasses['filePath'];\n    }\n}\n\n// Output the results\nforeach ($classes_and_paths as $class => $filepath) {\n    echo "$class => $filepath\n";\n}