<?php\n// The input string\n$input = "{ 1, 3, 1, 2, }, { 2, 3, 2, 1, }, { 3, 3, 2, 2, }, { 1, 2, 3, 1, },\n";\n\n// Remove any unwanted characters and split the string into individual sets\n$input = preg_replace('/\s+/', '', $input); // Remove all whitespace\n$input = trim($input, ','); // Remove trailing comma\n$sets = explode('},{', $input);\n\n// Clean up the sets\n$sets = array_map(function($set) {\n    return trim($set, '{}');\n}, $sets);\n\n// Convert each set into an array of integers\n$arrays = array_map(function($set) {\n    return array_map('intval', explode(',', $set));\n}, $sets);\n\n// Print out the arrays\nforeach ($arrays as $index => $array) {\n    echo "Array " . ($index + 1) . ":\n";\n    foreach ($array as $key => $value) {\n        echo "array" . ($index + 1) . "[$key] == $value\n";\n    }\n    echo "\n";\n}\n?>