// session_check.php\n<?php\nsession_start();\n\nif (!isset($_SESSION['user_id'])) {\n    header("HTTP/1.1 403 Forbidden");\n    exit('Access denied');\n}\n\n$file = $_GET['file'];\n$file_path = '/path/to/protected/html/files/' . $file;\n\nif (file_exists($file_path)) {\n    header('Content-Type: text/html');\n    readfile($file_path);\n} else {\n    header("HTTP/1.1 404 Not Found");\n    exit('File not found');\n}\n?>