<?php\n$file = 'path/to/your/file.txt'; // Specify the path to your file\n\n$lastSize = 0;\nwhile (true) {\n    clearstatcache();\n    $currentSize = filesize($file);\n\n    if ($currentSize > $lastSize) {\n        $fh = fopen($file, 'r');\n        fseek($fh, $lastSize);\n        $newContent = fread($fh, $currentSize - $lastSize);\n        fclose($fh);\n\n        echo $newContent; // Output the new content\n        flush();\n\n        $lastSize = $currentSize;\n    }\n\n    sleep(1); // Pause for a second before checking again\n}\n?>