<?php\nrequire 'class.phpmailer.php';\nrequire 'class.smtp.php';\n\n$mail = new PHPMailer();\n\n// Ensure the file path for contents.html is correct\n$body = file_get_contents('contents.html');\nif ($body === false) {\n    die('Error: Could not read contents.html');\n}\n\n$mail->IsSMTP();\n$mail->SMTPAuth = true;\n$mail->SMTPSecure = "ssl";\n$mail->Host = "smtp.gmail.com";\n$mail->Port = 465;\n$mail->Username = "hussaintalha@gmail.com";\n$mail->Password = "xxxxxxxx";\n\n$mail->AddReplyTo("hussaintalha@gmail.com", "First Last");\n$mail->From = "hussaintalha@gmail.com";\n$mail->FromName = "First Last";\n$mail->Subject = "PHPMailer Test Subject via gmail";\n$mail->AltBody = "To view the message, please use an HTML compatible email viewer!";\n$mail->WordWrap = 50;\n\n$mail->MsgHTML($body);\n$mail->AddAddress("hussaintalha@gmail.com", "John Doe");\n$mail->AddAttachment("images/phpmailer.gif");\n\n$mail->IsHTML(true);\n\nif (!$mail->Send()) {\n    echo "Mailer Error: " . $mail->ErrorInfo;\n} else {\n    echo "Message sent!";\n}\n?>