<?php\nrequire 'vendor/autoload.php';\n\nuse PhpMimeMailParser\Parser;\n\nfunction displayEmail($emailSource) {\n    $parser = new Parser();\n    $parser->setText($emailSource);\n\n    $html = $parser->getMessageBody('html');\n    $text = $parser->getMessageBody('text');\n\n    if ($html) {\n        echo $html;\n    } else {\n        echo nl2br(htmlentities($text));\n    }\n}\n\n// Example usage\n$emailSource = file_get_contents('path/to/email.eml');\ndisplayEmail($emailSource);\n?>