<?php\nclass Foo\n{\n    var $value;\n\n    function foo($value)\n    {\n        $this->setValue($value);\n    }\n\n    function setValue($value)\n    {\n        $this->value = $value;\n    }\n}\n\nclass Bar\n{\n    var $foos = array();\n\n    function Bar()\n    {\n        for ($x = 1; $x <= 10; $x++)\n        {\n            $this->foos[$x] = new Foo("Foo # $x");\n        }\n    }\n\n    function getFoo($index)\n    {\n        return $this->foos[$index];\n    }\n\n    function test()\n    {\n        $testFoo = $this->getFoo(5);\n        $testFoo->setValue("My value has now changed");\n    }\n}\n?>