<?php\n   // Database connection\n   $mysqli = new mysqli("hostname", "username", "password", "database");\n\n   // Email details\n   $recipient = $to;\n   $subject = "Subject";\n   $message = "Message";\n   $headers = 'From: '.$Name.''. "\r\n" .\n              'Reply-To: '.$_POST['email'].'' . "\r\n" .\n              'X-Mailer: PHP/' . phpversion();\n\n   // Calculate the scheduled time (one day later)\n   $scheduled_time = date('Y-m-d H:i:s', strtotime('+1 day'));\n\n   // Insert into the database\n   $stmt = $mysqli->prepare("INSERT INTO scheduled_emails (recipient, subject, message, headers, scheduled_time) VALUES (?, ?, ?, ?, ?)");\n   $stmt->bind_param("sssss", $recipient, $subject, $message, $headers, $scheduled_time);\n   $stmt->execute();\n   $stmt->close();\n   ?>