<?php\n$local_file = "path/to/localfile.tar.gz";\n$extracted_path = "path/to/extracted";\n\n// Create the extracted directory if it doesn't exist\nif (!is_dir($extracted_path)) {\n    mkdir($extracted_path, 0755, true);\n}\n\n// Extract the tar.gz file\n$phar = new PharData($local_file);\n$phar->decompress(); // Decompress the .gz\n$phar->extractTo($extracted_path, null, true); // Extract all files\n?>