<?php\nfunction get_request_headers() {\n    $headers = [];\n    foreach ($_SERVER as $key => $value) {\n        if (substr($key, 0, 5) == 'HTTP_') {\n            $header = str_replace(' ', '-', ucwords(str_replace('_', ' ', strtolower(substr($key, 5)))));\n            $headers[$header] = $value;\n        }\n    }\n    return $headers;\n}\n\n$headers = get_request_headers();\nprint_r($headers);\n?>