<?php\n$user_id = 1; // Replace with the actual user ID whose activities you want to list\n$dsn = 'mysql:host=your_host;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n    \n    $stmt = $pdo->prepare("\n        SELECT \n            activity_type, \n            reference_id, \n            activity_content, \n            created_at \n        FROM user_activities \n        WHERE user_id = :user_id \n        ORDER BY created_at DESC \n        LIMIT 10\n    ");\n    $stmt->execute(['user_id' => $user_id]);\n    $activities = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    \n    foreach ($activities as $activity) {\n        echo 'Activity Type: ' . htmlspecialchars($activity['activity_type']) . '<br>';\n        echo 'Content: ' . htmlspecialchars($activity['activity_content']) . '<br>';\n        echo 'Date: ' . htmlspecialchars($activity['created_at']) . '<br><br>';\n    }\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>