<?php\n// Define the CGI script path and parameters\n$script = "/path/to/your/cgi_script.cgi";\n$params = "param1=value1&param2=value2";\n\n// Build the command\n$command = escapeshellcmd($script . ' ' . $params);\n\n// Execute the CGI script\n$output = exec($command, $outputArray, $returnVar);\n\n// Handle the output\nif ($returnVar === 0) {\n    echo "CGI script executed successfully.";\n    echo implode("\n", $outputArray);\n} else {\n    echo "Error executing CGI script.";\n}\n?>