<?php\n$ftp_server = "ftp.example.com";\n$ftp_username = "your_username";\n$ftp_password = "your_password";\n$remote_file = "path/to/yourfile.tar.gz";\n$local_file = "path/to/localfile.tar.gz";\n\n// Establish an FTP connection\n$conn_id = ftp_connect($ftp_server);\n\n// Login with FTP credentials\nif (@ftp_login($conn_id, $ftp_username, $ftp_password)) {\n    // Download the tar.gz file\n    if (ftp_get($conn_id, $local_file, $remote_file, FTP_BINARY)) {\n        echo "Successfully downloaded $remote_file to $local_file\n";\n    } else {\n        echo "There was a problem downloading $remote_file\n";\n    }\n} else {\n    echo "Couldn't connect as $ftp_username\n";\n}\n\n// Close the FTP connection\nftp_close($conn_id);\n?>