function session_data_decode($sessionData) {\n    $result = [];\n    $offset = 0;\n\n    while ($offset < strlen($sessionData)) {\n        if (!strstr(substr($sessionData, $offset), "|")) {\n            return false;\n        }\n        $pos = strpos($sessionData, "|", $offset);\n        $num = $pos - $offset;\n        $varname = substr($sessionData, $offset, $num);\n        $offset += $num + 1;\n        $data = unserialize(substr($sessionData, $offset));\n        $result[$varname] = $data;\n        $offset += strlen(serialize($data));\n    }\n\n    return $result;\n}\n\n// Example usage\nsession_start();\n$_SESSION['foo'] = 'bar';\n$_SESSION['baz'] = 42;\n$encodedSessionData = session_encode();\n$decodedSessionData = session_data_decode($encodedSessionData);\nprint_r($decodedSessionData);