<?php\n// List of allowed IP ranges\n$allowed_ip_ranges = [\n    '10.*.*.*',\n    '200.10.10.*'\n];\n\n// Function to check if an IP is in the allowed range\nfunction ip_in_range($ip, $range) {\n    if (strpos($range, '*') !== false) {\n        $range = str_replace('*', '0', $range);\n        $range_prefix = substr($range, 0, strrpos($range, '.') + 1);\n        $ip_prefix = substr($ip, 0, strrpos($range, '.') + 1);\n        return $range_prefix === $ip_prefix;\n    }\n    return $ip === $range;\n}\n\n// Get the client's IP address\n$client_ip = $_SERVER['REMOTE_ADDR'];\n\n// Check if the client's IP address is in the allowed range\n$access_granted = false;\nforeach ($allowed_ip_ranges as $range) {\n    if (ip_in_range($client_ip, $range)) {\n        $access_granted = true;\n        break;\n    }\n}\n\n// Deny access if the IP is not in the allowed range\nif (!$access_granted) {\n    header('HTTP/1.0 403 Forbidden');\n    echo 'You are not allowed to access this page.';\n    exit;\n}\n\n// Your protected content goes here\necho 'Welcome! You have access to this page.';\n?>