<?php\n// index.php\n\n// Start output buffering\nob_start();\n\n// Include the script\ninclude "script.php";\n\n// Capture the output\n$out = ob_get_clean();\n\n// Check if any headers are set\n$headers = headers_list();\n\n// Loop through the headers and remove any 'Location' header\nforeach ($headers as $header) {\n    if (stripos($header, 'Location:') === 0) {\n        header_remove('Location');\n    }\n}\n\n// Process the captured output\n// (e.g., use $out data for calculations)\n\n// Output the final content\necho $out;\n?>