// Example price calculation function\nfunction calculateTotal($items) {\n    $total = 0;\n    foreach ($items as $item) {\n        if ($item['price'] <= 0) {\n            // Log an error or handle invalid price\n            logError("Invalid price detected for item ID: " . $item['id']);\n            continue;\n        }\n        $total += $item['price'] * $item['quantity'];\n    }\n    return $total;\n}