public function userProposals() {\n    $username = User::getUsername();\n    $state = User::userState();\n    \n    $sql = "\n        SELECT id, title, 'petition' AS type FROM petition WHERE author = '$username'\n        UNION ALL\n        SELECT id, title, 'proposition' AS type FROM proposition WHERE author = '$username'\n        UNION ALL\n        SELECT id, title, 'amendment' AS type FROM amendment WHERE author = '$username'\n    ";\n    \n    $query = mysql_query($sql);\n    \n    while ($row = mysql_fetch_assoc($query)) {\n        echo "\n        <tr>\n            <td>{$row['id']}</td>\n            <td><a href='viewproposal.php?type={$row['type']}&id={$row['id']}'>{$row['title']}</a></td>\n            <td>{$row['type']}</td>\n            <td>$state</td>\n        </tr>";\n    }\n}