<?php\nrequire 'db_connection.php'; // Include your database connection\n\n// Calculate the start of the week (e.g., last Monday)\n$start_of_week = date('Y-m-d H:i:s', strtotime('monday this week'));\n\n// Get the most voted question of the week\n$stmt = $conn->prepare("\n    SELECT q.id, q.question_text, COUNT(v.id) as vote_count \n    FROM questions q\n    LEFT JOIN votes v ON q.id = v.question_id\n    WHERE q.created_at >= ?\n    GROUP BY q.id\n    ORDER BY vote_count DESC\n    LIMIT 1\n");\n$stmt->bind_param("s", $start_of_week);\n$stmt->execute();\n$result = $stmt->get_result();\n$best_question = $result->fetch_assoc();\n$stmt->close();\n\necho "Question of the week: " . $best_question['question_text'] . " with " . $best_question['vote_count'] . " votes.";\n?>