<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\IOFactory;\n\n$inputFileName = 'path/to/your/excel-file.xls'; // Path to the Excel file\n\n// Identify the type of $inputFileName\n$inputFileType = IOFactory::identify($inputFileName);\n\n// Create a Reader object for the Excel file type\n$reader = IOFactory::createReader($inputFileType);\n\n// Load the file into a Spreadsheet object\n$spreadsheet = $reader->load($inputFileName);\n\n// Get the active sheet\n$sheet = $spreadsheet->getActiveSheet();\n\n// Get the highest row and column numbers referenced in the sheet\n$highestRow = $sheet->getHighestRow();\n$highestColumn = $sheet->getHighestColumn();\n\n// Iterate through each row and column to read the data\nfor ($row = 1; $row <= $highestRow; ++$row) {\n    for ($col = 'A'; $col <= $highestColumn; ++$col) {\n        $cellValue = $sheet->getCell($col . $row)->getValue();\n        echo "Cell " . $col . $row . ": " . $cellValue . PHP_EOL;\n    }\n}\n?>