<?php\n// Function to add a new user\nfunction addUser($username, $password) {\n    $command = sprintf('sudo useradd %s', escapeshellarg($username));\n    shell_exec($command);\n    $command = sprintf('echo %s:%s | sudo chpasswd', escapeshellarg($username), escapeshellarg($password));\n    shell_exec($command);\n}\n\n// Function to delete a user\nfunction deleteUser($username) {\n    $command = sprintf('sudo userdel %s', escapeshellarg($username));\n    shell_exec($command);\n}\n\n// Function to check if a user exists\nfunction userExists($username) {\n    $command = sprintf('id -u %s 2>/dev/null', escapeshellarg($username));\n    $output = shell_exec($command);\n    return !empty($output);\n}\n\n// Example usage\n$username = 'exampleuser';\n$password = 'examplepassword';\n\nif (!userExists($username)) {\n    addUser($username, $password);\n    echo "User $username added successfully.";\n} else {\n    echo "User $username already exists.";\n}\n?>