<?php\n// Database connection details\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to select display and link columns\n$sql = "SELECT display, link FROM links";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Output data of each row\n    while($row = $result->fetch_assoc()) {\n        $display = htmlspecialchars($row["display"], ENT_QUOTES, 'UTF-8');\n        $link = htmlspecialchars($row["link"], ENT_QUOTES, 'UTF-8');\n        echo "<a href='$link'>$display</a><br>";\n    }\n} else {\n    echo "0 results";\n}\n\n// Close connection\n$conn->close();\n?>