// Example logging function\nfunction logTransaction($orderId, $userId, $productId, $productPrice, $orderTotal, $timestamp) {\n    $logData = sprintf("Order ID: %s, User ID: %s, Product ID: %s, Product Price: %s, Order Total: %s, Timestamp: %s\n", \n                        $orderId, $userId, $productId, $productPrice, $orderTotal, $timestamp);\n    file_put_contents('transaction_log.txt', $logData, FILE_APPEND);\n}