<?php\n// URL of the file on the remote server\n$remoteFileUrl = 'http://example.com/file.zip';\n\n// The new filename you want to use\n$newFileName = 'your_new_filename.zip';\n\n// Open a file handle to the remote file\n$fileHandle = fopen($remoteFileUrl, 'rb');\n\nif ($fileHandle) {\n    // Send the appropriate headers to force download with the new filename\n    header('Content-Type: application/zip');\n    header('Content-Disposition: attachment; filename="' . $newFileName . '"');\n    header('Content-Transfer-Encoding: binary');\n\n    // Read and output the file in chunks\n    while (!feof($fileHandle)) {\n        echo fread($fileHandle, 8192);\n    }\n\n    // Close the file handle\n    fclose($fileHandle);\n} else {\n    // Handle errors\n    header("HTTP/1.0 404 Not Found");\n    echo 'File not found.';\n}\n?>