$newshottitle = "My new screenshot";\n$newshotlink = "http://www.image.com/image.jpg";\n\n$dom = new DOMDocument();\n$dom->formatOutput = true;\n$dom->load("../xml/screenshots.xml");\n\n// Locate the <screenshots> element\n$screenshots = $dom->getElementsByTagName("screenshots")->item(0);\n\n// Create a new <item> element\n$item = $dom->createElement("item");\n\n// Create and append the <title> element to <item>\n$title = $dom->createElement("title", $newshottitle);\n$item->appendChild($title);\n\n// Create and append the <link> element to <item>\n$link = $dom->createElement("link", $newshotlink);\n$item->appendChild($link);\n\n// Append the new <item> element to the <screenshots> element\n$screenshots->appendChild($item);\n\n// Save the modified XML document\n$dom->save("../xml/screenshots.xml");