<?php\n\n// Define a simple class\nclass MyObject {\n    public $property;\n\n    public function __construct($property) {\n        $this->property = $property;\n    }\n}\n\n// Create an array to hold objects\n$arrayList = array();\n\n// Create objects and add them to the array\n$obj1 = new MyObject("Value 1");\n$obj2 = new MyObject("Value 2");\n\n$arrayList[] = $obj1;\n$arrayList[] = $obj2;\n\n// Access the objects from the array\nforeach ($arrayList as $obj) {\n    echo $obj->property . "\n";\n}\n?>