<?php\n// Database connection (replace with your own connection details)\n$conn = new mysqli('servername', 'username', 'password', 'database');\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to fetch the data\n$sql = "SELECT link_id, url, category FROM your_table ORDER BY link_id";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    // Initialize an associative array to store the results\n    $data = [];\n\n    // Fetch results and organize them by link_id\n    while ($row = $result->fetch_assoc()) {\n        $link_id = $row['link_id'];\n        $url = $row['url'];\n        $category = $row['category'];\n\n        if (!isset($data[$link_id])) {\n            $data[$link_id] = ['url' => $url, 'categories' => []];\n        }\n        $data[$link_id]['categories'][] = $category;\n    }\n\n    // Output the results\n    foreach ($data as $link_id => $info) {\n        $url = $info['url'];\n        $categories = implode(' ', $info['categories']);\n        echo "link_id = $link_id   url = '$url'   category = '$categories'\n";\n    }\n} else {\n    echo "No results found.";\n}\n\n$conn->close();\n?>