function procLogin($username, $password) {\n    try {\n        // Create a new PDO instance\n        $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Prepare the SQL statement\n        $stmt = $pdo->prepare("SELECT * FROM members WHERE login = :username AND passwd = :password");\n\n        // Bind parameters\n        $stmt->bindParam(':username', $username);\n        $stmt->bindParam(':password', $password);\n\n        // Execute the statement\n        $stmt->execute();\n\n        // Fetch the result\n        $row = $stmt->fetch(PDO::FETCH_ASSOC);\n\n        if ($row) {\n            return array($row['member_id']);\n        } else {\n            return 'Invalid login credentials';\n        }\n    } catch (PDOException $e) {\n        return 'Database error: ' . $e->getMessage();\n    }\n}