<?php\n$postarray = array(1, 2, 3); // Example array of post IDs\n\n$args = array(\n    'post_type' => 'post',\n    'post__in' => $postarray,\n    'orderby' => 'post__in', // Ensures the order of posts matches the order in $postarray\n);\n\n$the_query = new WP_Query($args);\n\nif ($the_query->have_posts()) :\n    while ($the_query->have_posts()) : $the_query->the_post();\n        the_title('<h2>', '</h2>');\n        the_excerpt();\n    endwhile;\n    wp_reset_postdata();\nelse :\n    echo '<p>No posts found.</p>';\nendif;\n?>