// Assuming you have a database connection $mysqli and $result is your query result\n$returnArray = array();\nwhile ($row = $result->fetch_assoc()) {\n    $returnArray[$row['customer_id']] = $row['cust'];\n}\n$result->free();\n\n// Assuming $q is your search query\n$q = strtolower($q);\n\nforeach ($returnArray as $key => $value) {\n    if (strpos(strtolower($value), $q) !== false) {\n        echo "$key|$value\n";\n    }\n}