<?php\n    session_start();\n    $userId = $_SESSION['user_id']; // Assuming you store the user ID in session\n\n    $db = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $stmt = $db->prepare("SELECT widget_id, position FROM widget_positions WHERE user_id = :user_id ORDER BY position");\n    $stmt->execute([':user_id' => $userId]);\n    $positions = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    ?>\n\n    <div id="widget-container">\n        <?php foreach ($positions as $position): ?>\n            <div class="widget" data-widget-id="<?php echo htmlspecialchars($position['widget_id']); ?>">\n                <?php echo htmlspecialchars($position['widget_id']); ?>\n            </div>\n        <?php endforeach; ?>\n    </div>