<?php\n// Ensure the raw post data is available\n$data = file_get_contents('php://input');\nif ($data === false) {\n    die('Failed to read input data.');\n}\n\n// Define the file path\n$newfile = '/path/to/directory/filename';\n$filepath = $newfile . '.jpg';\n\n// Open the file for writing\n$handle = fopen($filepath, 'wb');\nif ($handle === false) {\n    die('Failed to open file for writing.');\n}\n\n// Write the data to the file\nif (fwrite($handle, $data) === false) {\n    fclose($handle);\n    die('Failed to write data to file.');\n}\n\n// Close the file handle\nfclose($handle);\n\n// Print the new file path\necho htmlspecialchars($filepath, ENT_QUOTES, 'UTF-8');\n?>