<?php\n// Daemon script to check MySQL, run FFmpeg, and insert output\n\n// Database connection\n$mysqli = new mysqli("host", "user", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\nwhile (true) {\n    // Check for new rows\n    $result = $mysqli->query("SELECT * FROM your_table WHERE processed = 0");\n\n    while ($row = $result->fetch_assoc()) {\n        $id = $row['id'];\n        $input = $row['input_file'];\n\n        // Run FFmpeg command\n        $output = "/path/to/output_$id.mp4";\n        $command = "ffmpeg -i $input $output";\n        shell_exec($command);\n\n        // Insert output into database\n        $stmt = $mysqli->prepare("UPDATE your_table SET output_file = ?, processed = 1 WHERE id = ?");\n        $stmt->bind_param("si", $output, $id);\n        $stmt->execute();\n    }\n\n    // Sleep for a while before checking again\n    sleep(10);\n}\n?>