use PHPUnit\Framework\TestCase;\n\nclass MyClassTest extends TestCase {\n    public function testMyMethod() {\n        // Create a mock for the OtherClassInterface\n        $otherClassMock = $this->createMock(OtherClassInterface::class);\n\n        // Define the behavior of the mock\n        $otherClassMock->method('someMethod')->willReturn('expectedValue');\n\n        // Create an instance of MyClass with the mock\n        $myClass = new MyClass($otherClassMock);\n\n        // Call the method and assert the expected result\n        $result = $myClass->myMethod();\n\n        // Add your assertions here\n        $this->assertEquals('expectedValue', $result);\n    }\n}