// Using PHP include to display WordPress content\n   // This requires setting up a WordPress loop in your existing PHP file.\n   define('WP_USE_THEMES', false);\n   require('path/to/your/wordpress/directory/wp-blog-header.php');\n   \n   // Query for the latest blog posts\n   query_posts('showposts=5');\n   while (have_posts()) : the_post(); ?>\n       <h2><?php the_title(); ?></h2>\n       <div><?php the_content(); ?></div>\n   <?php endwhile;