<?php\nfunction isSearchEngineBot() {\n    $bots = [\n        'Googlebot',\n        'Bingbot',\n        'Slurp',\n        'DuckDuckBot',\n        'Baiduspider',\n        'YandexBot',\n        'Sogou',\n        'Exabot',\n        'facebot',\n        'ia_archiver'\n    ];\n\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n\n    foreach ($bots as $bot) {\n        if (stripos($userAgent, $bot) !== false) {\n            return true;\n        }\n    }\n    return false;\n}\n\nif (isSearchEngineBot()) {\n    echo "Detected a search engine bot.";\n} else {\n    echo "No search engine bot detected.";\n}\n?>