function removeUrls($text) {\n    // Regular expression to match URLs starting with http, https, or www\n    $pattern = '/\b((http|https):\/\/|www\.)\S+\b/i';\n    \n    // Use preg_replace to remove the matched URLs\n    $result = preg_replace($pattern, '', $text);\n    \n    return $result;\n}\n\n// Example usage\n$url1 = "Here is a funny site http://www.tinyurl.com/34934";\n$url2 = "Here is another funny site www.tinyurl.com/55555";\n\necho removeUrls($url1); // Output: Here is a funny site \necho removeUrls($url2); // Output: Here is another funny site