<?php\n// Ensure browscap.ini is set in php.ini file\n// example: browscap = /path/to/browscap.ini\n\n$browser = get_browser(null, true);\n\nif ($browser['browser'] == 'IE' && $browser['version'] == '7.0') {\n    echo 'The browser is Internet Explorer 7';\n} elseif ($browser['browser'] == 'IE' && $browser['version'] == '6.0') {\n    echo 'The browser is Internet Explorer 6';\n} else {\n    echo 'The browser is not Internet Explorer 6 or 7';\n}\n?>