function hasNonZeroValue($array) {\n    foreach ($array as $value) {\n        if (is_array($value)) {\n            if (hasNonZeroValue($value)) {\n                return true;\n            }\n        } else {\n            if ($value != 0) {\n                return true;\n            }\n        }\n    }\n    return false;\n}\n\n// Test cases\n$array1 = array(\n    'test' => array(0, 0, 0, array(0, 0, 0))\n);\n$array2 = array(\n    'test' => array(0, 0, 0, array(6, 0, 0)) // notice the 6\n);\n\nvar_dump(hasNonZeroValue($array1)); // Should return false\nvar_dump(hasNonZeroValue($array2)); // Should return true