<?php\n$domains = ['domain1.com', 'domain2.com'];\n\nforeach ($domains as $domain) {\n    echo "<h2>Managing $domain</h2>";\n    // Example: Display files in a specific directory for each domain\n    $dir = "/var/www/$domain/public_html";\n    if (is_dir($dir)) {\n        if ($dh = opendir($dir)) {\n            while (($file = readdir($dh)) !== false) {\n                echo "filename: $file <br />";\n            }\n            closedir($dh);\n        }\n    }\n}\n\n// Upload file to selected domain\nif (isset($_POST['upload'])) {\n    $target_dir = "/var/www/" . $_POST['domain'] . "/public_html/";\n    $target_file = $target_dir . basename($_FILES["fileToUpload"]["name"]);\n    if (move_uploaded_file($_FILES["fileToUpload"]["tmp_name"], $target_file)) {\n        echo "The file ". basename($_FILES["fileToUpload"]["name"]). " has been uploaded.";\n    } else {\n        echo "Sorry, there was an error uploading your file.";\n    }\n}\n?>\n\n<form method="post" enctype="multipart/form-data">\n    Select domain:\n    <select name="domain">\n        <option value="domain1.com">domain1.com</option>\n        <option value="domain2.com">domain2.com</option>\n        <!-- Add more domains as needed -->\n    </select>\n    Select file to upload:\n    <input type="file" name="fileToUpload" id="fileToUpload">\n    <input type="submit" value="Upload File" name="upload">\n</form>