<?php\n    header('Content-Type: application/json');\n    $rss_feed = simplexml_load_file('http://example.com/rss');\n    $news_items = [];\n    for ($i = 0; $i < 2; $i++) {\n        $news_items[] = [\n            'title' => (string)$rss_feed->channel->item[$i]->title,\n            'link' => (string)$rss_feed->channel->item[$i]->link,\n            'description' => (string)$rss_feed->channel->item[$i]->description\n        ];\n    }\n    echo json_encode($news_items);\n    ?>