<?php\n// Assuming you have the Serial class available\n$serial = new Serial();\n\n// Initialize the serial port\n$serial->deviceSet("/dev/ttyS0"); // Set the device name accordingly\n$serial->confBaudRate(9600); // Set the baud rate\n\n// Open the serial port\n$serial->deviceOpen();\n\n// Send a single byte, for example, the byte value 1\n$byteToSend = chr(1);\n$serial->sendMessage($byteToSend);\n\n// Close the serial port\n$serial->deviceClose();\n?>