for($i = 0; $i < count($_FILES['uploadimg']['name']); $i++) {\n    $name = $_FILES['uploadimg']['name'][$i];\n    $type = $_FILES['uploadimg']['type'][$i];\n    $filepath = $_FILES['uploadimg']['tmp_name'][$i];\n    $error = $_FILES['uploadimg']['error'][$i];\n\n    if ($error === UPLOAD_ERR_OK) {\n        $size = getimagesize($filepath);\n        $img = file_get_contents($filepath);\n\n        // Insert into database\n        // Ensure to use prepared statements to prevent SQL injection\n    } else {\n        switch ($error) {\n            case UPLOAD_ERR_INI_SIZE:\n            case UPLOAD_ERR_FORM_SIZE:\n                echo "File {$name} is too large.";\n                break;\n            case UPLOAD_ERR_PARTIAL:\n                echo "File {$name} was only partially uploaded.";\n                break;\n            case UPLOAD_ERR_NO_FILE:\n                echo "No file was uploaded.";\n                break;\n            case UPLOAD_ERR_NO_TMP_DIR:\n                echo "Missing a temporary folder.";\n                break;\n            case UPLOAD_ERR_CANT_WRITE:\n                echo "Failed to write file to disk.";\n                break;\n            case UPLOAD_ERR_EXTENSION:\n                echo "A PHP extension stopped the file upload.";\n                break;\n            default:\n                echo "Unknown upload error.";\n                break;\n        }\n    }\n}