<?php\n// Get the current date\n$currentDay = date('j');\n$currentMonth = date('n');\n$currentYear = date('Y');\n\n// Get the first day of the current month\n$firstDayOfMonth = mktime(0, 0, 0, $currentMonth, 1, $currentYear);\n\n// Get the number of days in the current month\n$daysInMonth = date('t', $firstDayOfMonth);\n\n// Get the day of the week the first day of the month falls on\n$firstDayOfWeek = date('w', $firstDayOfMonth);\n\n// Define an array for the days of the week\n$daysOfWeek = array('Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat');\n\necho '<table>';\necho '<tr>';\n// Display the days of the week\nforeach ($daysOfWeek as $day) {\n    echo "<th>$day</th>";\n}\necho '</tr><tr>';\n\n// Fill in the blanks before the first day of the month\nfor ($i = 0; $i < $firstDayOfWeek; $i++) {\n    echo '<td></td>';\n}\n\n// Display the days of the month\nfor ($day = 1; $day <= $daysInMonth; $day++) {\n    if ($day == $currentDay) {\n        // Highlight the current day\n        echo '<td id="current-day">' . $day . '</td>';\n    } else {\n        echo '<td id="row-calendar-cell">' . $day . '</td>';\n    }\n\n    // Start a new row after every Saturday\n    if (($day + $firstDayOfWeek) % 7 == 0) {\n        echo '</tr><tr>';\n    }\n}\n\n// Fill in the blanks after the last day of the month\nwhile (($day + $firstDayOfWeek) % 7 != 1) {\n    echo '<td></td>';\n    $day++;\n}\n\necho '</tr>';\necho '</table>';\n?>