<?php\n// Start the session\nsession_start();\n\n// Your proxy script URL\n$proxyUrl = "http://example.com/remote-script.php";\n\n// Initialize the HTTP request\n$request = new HttpRequest($proxyUrl, HttpRequest::METH_GET);\n\n// Include the PHPSESSID in the headers\nif (isset($_COOKIE['PHPSESSID'])) {\n    $request->setHeaders(array(\n        'Cookie' => 'PHPSESSID=' . $_COOKIE['PHPSESSID']\n    ));\n}\n\n// Send the request\ntry {\n    $response = $request->send();\n    echo $response->getBody();\n} catch (HttpException $ex) {\n    echo $ex;\n}\n?>