<?php\n// Define metadata for each page\n$metadata = [\n    'home' => [\n        'title' => 'Home Page',\n        'keywords' => 'home, index, main',\n        'description' => 'This is the home page description.'\n    ],\n    'about' => [\n        'title' => 'About Us',\n        'keywords' => 'about, information, company',\n        'description' => 'This is the about page description.'\n    ],\n    'contact' => [\n        'title' => 'Contact Us',\n        'keywords' => 'contact, email, phone',\n        'description' => 'This is the contact page description.'\n    ]\n];\n\n// Determine the current page (you can modify this logic based on your routing)\n$page = basename($_SERVER['PHP_SELF'], '.php');\n\n// Set default metadata if the page is not in the array\n$title = $metadata[$page]['title'] ?? 'Default Title';\n$keywords = $metadata[$page]['keywords'] ?? 'default, keywords';\n$description = $metadata[$page]['description'] ?? 'Default description.';\n\n?>\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title><?php echo htmlspecialchars($title); ?></title>\n    <meta name="keywords" content="<?php echo htmlspecialchars($keywords); ?>" />\n    <meta name="description" content="<?php echo htmlspecialchars($description); ?>" />\n</head>\n<body>\n    <!-- Page content goes here -->\n</body>\n</html>