function strClean($input) {\n    // Ensure the input is in UTF-8\n    $input = mb_convert_encoding($input, 'UTF-8', 'UTF-8');\n    \n    // Convert to lowercase using mb_strtolower for UTF-8\n    $input = mb_strtolower($input, 'UTF-8');\n    \n    // Define the characters to replace\n    $b = array("á","é","í","ó","ú", "ñ", " ");\n    $c = array("a","e","i","o","u","n", "-");\n    \n    // Replace the characters\n    $input = str_replace($b, $c, $input);\n    \n    return $input;\n}