function svn_move($oldPath, $newPath) {\n    // Ensure the old path exists and is under version control\n    if (!file_exists($oldPath)) {\n        throw new Exception("The path $oldPath does not exist.");\n    }\n\n    // Execute the SVN move command\n    $command = escapeshellcmd("svn move " . escapeshellarg($oldPath) . " " . escapeshellarg($newPath));\n    $output = [];\n    $return_var = 0;\n    exec($command, $output, $return_var);\n\n    if ($return_var !== 0) {\n        throw new Exception("Failed to move $oldPath to $newPath. SVN Error: " . implode("\n", $output));\n    }\n\n    echo "Moved $oldPath to $newPath successfully.\n";\n}\n\nfunction svn_delete($path) {\n    // Ensure the path exists and is under version control\n    if (!file_exists($path)) {\n        throw new Exception("The path $path does not exist.");\n    }\n\n    // Execute the SVN delete command\n    $command = escapeshellcmd("svn delete " . escapeshellarg($path));\n    $output = [];\n    $return_var = 0;\n    exec($command, $output, $return_var);\n\n    if ($return_var !== 0) {\n        throw new Exception("Failed to delete $path. SVN Error: " . implode("\n", $output));\n    }\n\n    echo "Deleted $path successfully.\n";\n}\n\nfunction svn_add($path) {\n    // Ensure the path exists\n    if (!file_exists($path)) {\n        throw new Exception("The path $path does not exist.");\n    }\n\n    // Execute the SVN add command\n    $command = escapeshellcmd("svn add " . escapeshellarg($path));\n    $output = [];\n    $return_var = 0;\n    exec($command, $output, $return_var);\n\n    if ($return_var !== 0) {\n        throw new Exception("Failed to add $path. SVN Error: " . implode("\n", $output));\n    }\n\n    echo "Added $path to version control successfully.\n";\n}\n\n// Example usage:\ntry {\n    svn_move('somedir/foo', 'somedir/modified');\n    svn_delete('somedir/cheese');\n    svn_add('somedir/new_directory');\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage() . "\n";\n}